import React, { useState } from "react"


function SignUp(props){
    const [formData , setFormData] = useState({
        userName: "", password: "", confirmpassword: "" 
    })

    function handleChange(event){
        setFormData(prevFormData => {
            return {
                ...prevFormData,
                [event.target.name]: event.target.value
            }
        })
    }
    return(
        <div id="main-con">
            <form>
                <div className="mb-3">
                    <label htmlFor="username" className="form-label">userName</label>
                    <input type="text" className="form-control" placeholder="Username" onChange={handleChange} name="username" />
                </div>
                <div className="mb-3">
                    <label htmlFor="password" className="form-label">Password</label>
                    <input type="password" className="form-control" placeholder="password" onChange={handleChange} name="password" />
                </div>
                <div className="mb-3">
                    <label htmlFor="Confirmpassword" className="form-label">Password</label>
                    <input type="password" className="form-control" placeholder="Confirmpassword" onChange={handleChange} name="Confirmpassword" />
                </div>
                <button type="submit" className="btn btn-primary">Submit</button>
            </form>
        </div>
    )
    }
    export default SignUp
